void HairAssetDescriptor::initDefaults(void)
{

	freeStrings();
	freeReferences();
	freeDynamicArrays();
	numGuideHairs = uint32_t(0);
	numVertices = uint32_t(0);
	numFaces = uint32_t(0);
	numBones = uint32_t(0);
	numBoneSpheres = uint32_t(0);
	numBoneCapsules = uint32_t(0);
	numPinConstraints = uint32_t(0);
	sceneUnit = float(1.0);
	upAxis = uint32_t(0);
	handedness = uint32_t(0);

	initDynamicArrays();
	initStrings();
	initReferences();
}

